                                    //                                    dPlayer.player_type == PlayerType.Player &&
                                    //                                    _baserapeCausingPlayers.ContainsKey(dPlayer.player_name)).OrderBy(dPlayer => dPlayer.player_clanTag).ToList();
                                    if (randomBRCPlayers.Count > 1)
                                    {
                                        AdKatsTeam team1;
                                        AdKatsTeam team2;
