        public void PostPlayerWinLossStatistics()
        {
            Log.Debug("Entering PostPlayerWinLossStatistics", 7);
            try
            {
                if (_roundID < 1)
                {
                    return;
                }
                AdKatsTeam team1, team2;
                if (!GetTeamByID(1, out team1))
                {
                    if (_roundState == RoundState.Playing)
                    {
                        Log.Error("Teams not loaded when they should be.");
                    }
                    return;
                }
                if (!GetTeamByID(2, out team2))
                {
                    if (_roundState == RoundState.Playing)
                    {
                        Log.Error("Teams not loaded when they should be.");
                    }
                    return;
                }
                AdKatsTeam winningTeam, losingTeam;
                if (team1.TeamTicketCount > team2.TeamTicketCount)
                {
                    winningTeam = team1;
                    losingTeam = team2;
                }
                else
                {
                    winningTeam = team2;
                    losingTeam = team1;
                }
                List<AdKatsPlayer> WinningPlayers = _PlayerDictionary.Values.Where(aPlayer => aPlayer.frostbitePlayerInfo.TeamID == winningTeam.TeamID && aPlayer.player_type == PlayerType.Player).OrderByDescending(aPlayer => aPlayer.frostbitePlayerInfo.Score).ToList();
                List<AdKatsPlayer> LosingPlayers = _PlayerDictionary.Values.Where(aPlayer => aPlayer.frostbitePlayerInfo.TeamID == losingTeam.TeamID && aPlayer.player_type == PlayerType.Player).OrderByDescending(aPlayer => aPlayer.frostbitePlayerInfo.Score).ToList();
                foreach (AdKatsPlayer aPlayer in WinningPlayers)
                {
                    QueueStatisticForProcessing(new AdKatsStatistic()
                    {
                        stat_type = AdKatsStatistic.StatisticType.player_win,
                        server_id = _serverInfo.ServerID,
                        round_id = _roundID,
                        target_name = aPlayer.player_name,
                        target_player = aPlayer,
                        stat_value = aPlayer.frostbitePlayerInfo.SquadID,
                        stat_comment = aPlayer.player_name + " won",
                        stat_time = UtcDbTime()
                    });
                }
                foreach (AdKatsPlayer aPlayer in LosingPlayers)
                {
                    QueueStatisticForProcessing(new AdKatsStatistic()
                    {
                        stat_type = AdKatsStatistic.StatisticType.player_loss,
                        server_id = _serverInfo.ServerID,
                        round_id = _roundID,
                        target_name = aPlayer.player_name,
                        target_player = aPlayer,
                        stat_value = aPlayer.frostbitePlayerInfo.SquadID,
                        stat_comment = aPlayer.player_name + " lost",
                        stat_time = UtcDbTime()
                    });
                }
                if (_surrenderAutoTriggerCountCurrent > 0 && winningTeam.TeamTicketDifferenceRate > losingTeam.TeamTicketDifferenceRate)
                {
                    foreach (AdKatsPlayer aPlayer in WinningPlayers.Take((Int32)(WinningPlayers.Count / 3.5)).ToList())
                    {
                        QueueStatisticForProcessing(new AdKatsStatistic()
                        {
                            stat_type = AdKatsStatistic.StatisticType.player_baserape,
                            server_id = _serverInfo.ServerID,
                            round_id = _roundID,
                            target_name = aPlayer.player_name,
                            target_player = aPlayer,
                            stat_value = aPlayer.frostbitePlayerInfo.SquadID,
                            stat_comment = aPlayer.player_name + " baseraped from " + GetPlayerTeamKey(aPlayer) + " in position " + (WinningPlayers.IndexOf(aPlayer) + 1),
                            stat_time = UtcDbTime()
                        });
                    }
                }
            }
            catch (Exception e)
            {
                HandleException(new AdKatsException("Error while preparing map stats for upload", e));
            }
            Log.Debug("Exiting PostPlayerWinLossStatistics", 7);
        }

