                                                    _threadMasterWaitHandle.WaitOne(5000);
                                                    PlayerSayMessage(aPlayer.player_name, "Baserape monitor has you under temporary autobalance dispersion. Stats kept for " + _BaserapeCausingPlayersDurationDays + " days.");
                                                }
