	                            `command_type` = 88
                            AND 
	                            DATE_ADD(`record_time`, INTERVAL @duration_minutes MINUTE) > UTC_TIMESTAMP()
                            GROUP BY
	                            `target_id`
                            ORDER BY
	                            `population_count` DESC, `target_name` ASC) AS InnerResults
                            WHERE
	                            `population_count` >= @population_minimum";
