                            AllCapsLimiterWarnThreshold = 1;
                        }
                        if (_threadsReady)
                        {
                            if (AllCapsLimiterWarnThreshold >= _AllCapsLimiterKillThreshold)
                            {
                                Log.Error("All Caps Limiter Warn Threshold must be less than All Caps Limiter Kill Threshold.");
