                                    Log.Error("Could not find overview statistics for " + aPlayer.player_name);
                                }
                                else
                                {
                                    if (!overview.ContainsKey("scorePerMinute"))
                                    {
                                        Log.Error("Could not find BF4 SPM for " + aPlayer.player_name);
