                            }
                            else if (overviewResponse.Contains("errorpage"))
                            {
                                Log.Warn("Battlelog returning errors, waiting 30 seconds.");
                                _LastBattlelogAction = UtcNow().AddSeconds(30);
                                _LastBattlelogIssue = UtcNow();
