                                    response = ClientDownloadTimer(client, @"http://api.bfhstats.com/api/playerInfo?plat=pc&name=" + aPlayer.player_name + "&opt=&output=json");
                                    Hashtable json = (Hashtable)JSON.JsonDecode(response);

                                    if (json.ContainsKey("player"))
